--기존에 존재하는 route 테이블 삭제
DROP TABLE IF EXISTS route;
--테이블 column 추가
CREATE TABLE route (
                       id INT PRIMARY KEY,
                       destination VARCHAR(255),
                       startingpoint VARCHAR(255),
                       route VARCHAR(255),
                       emoji0 VARCHAR(255),
                       text1 VARCHAR(255),
                       emoji1 VARCHAR(255),
                       text2 VARCHAR(255),
                       emoji2 VARCHAR(255),
                       text3 VARCHAR(255),
                       emoji3 VARCHAR(255),
                       text4 VARCHAR(255),
                       emoji4 VARCHAR(255),
                       text5 VARCHAR(255),
                       emoji5 VARCHAR(255),
                       text6 VARCHAR(255),
                       emoji6 VARCHAR(255),
                       text7 VARCHAR(255),
                       emoji7 VARCHAR(255)
);
